import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { useRenderAfterLogin } from "@bsorrentino/xrmtoolboxweb-core";

show( 'hello')
export function App() {
  const { instance, account, scopes, acquireTokenSilent, renderAfterLogin } =
    useRenderAfterLogin();
  const [result, setResult] = useState < Partial < Response >>({});

  useEffect(() => {
    if (account) {
      acquireTokenSilent().then(Invoke).then(setResult);
    }
  }, [account?.localAccountId, instance]);

  return renderAfterLogin(() => (
    <div>
      <h3>Scope: {scopes[0]}</h3>
      <hr />
      <table>
        <tr><td>UserId:</td><td>{result.UserId}</td></tr>
        <tr><td>BusinessUnitId:</td><td>{result.BusinessUnitId}</td></tr>
        <tr><td>OrganizationId:</td><td>{result.OrganizationId}</td></tr>
      </table>
    </div>
  ));
}

import React from "react";
import ReactDOM from "react-dom";

import { MsalProvider } from "@azure/msal-react";

import { PCA } from '@bsorrentino/xrmtoolboxweb-core'


// Component
const AppProvider = () => (
    <MsalProvider instance={PCA}>
        <App />
    </MsalProvider>
);

show( <AppProvider /> )
